<pre class="brush:c">//
//  main.m
//  Shapes-Procedural
//
//  Created by hang liu on 12-1-15.
//  Copyright 2012年 __MyCompanyName__. All rights reserved.
//

#import
//定义形状
typedef enum {
    kCircle,
    kRectangle,
    kOblateSpheroid
}ShapeType;

//定义形状可用颜色
typedef enum{
    kRedColor,
    kGreenColor,
    kBlueColor
}ShapeColor;
//使用一个结构来描绘矩形
typedef struct {
    int x, y, windth, height;
}ShapeRect;
//将所有内容集合起来 描绘形状
typedef struct {
    ShapeType type;
    ShapeColor fillcolor;
    ShapeRect bounds;
} Shape;

NSString *colorName (ShapeColor colorName)
{
    switch (colorName){
        case kRedColor:
            return @"red";
            break;
        case kGreenColor:
            return @"green";
            break;
        case kBlueColor:
            return @"blue";
            break;
    }
    return @"no clue";
}//colorName

void drawCircle (ShapeRect bounds,
                 ShapeColor fillColor){
    NSLog (@"drawing a circle at (%d %d %d %d) in %@",
           bounds.x, bounds.y,
           bounds.windth, bounds.height,
           colorName(fillColor));

}//drawCircle

void drawRectangle (ShapeRect bounds,
					ShapeColor fillColor)
{
	NSLog (@"drawing a rectangle at (%d %d %d %d) in %@",
		   bounds.x, bounds.y,
		   bounds.windth, bounds.height,
		   colorName(fillColor));

} // drawRectangle

void drawEgg (ShapeRect bounds,
			  ShapeColor fillColor)
{
	NSLog (@"drawing an egg at (%d %d %d %d) in %@",
		   bounds.x, bounds.y,
		   bounds.windth, bounds.height,
		   colorName(fillColor));

} // drawEgg

void drawShapes (Shape shapes[], int count){
    int i;

    for (i = 0; i &lt; count; i++){
        switch (shapes[i].type){

            case kCircle:
                drawCircle (shapes[i].bounds,
                           shapes[i].fillcolor);
                break;

            case kRectangle:
                drawRectangle (shapes[i].bounds,
                               shapes[i].fillcolor);
                break;

            case kOblateSpheroid:
                drawEgg (shapes[i].bounds,
                         shapes[i].fillcolor);
                break;
        }
    }

}//drawShapes
int main (int argc, const char * argv[])
{

    Shape shapes[3];

    ShapeRect rect0 = {0, 0, 10, 30 };  //rect0的x = 0 y = 0 windth = 10 height = 30
    shapes[0].type = kCircle;
    shapes[0].fillcolor = kRedColor;
    shapes[0].bounds = rect0;

    ShapeRect rect1 = {30, 40, 50, 60 };
    shapes[1].type = kRectangle;
    shapes[1].fillcolor = kGreenColor;
    shapes[1].bounds = rect1;

    ShapeRect rect2 = {15, 18, 37, 29 };
    shapes[2].type = kOblateSpheroid;
    shapes[2].fillcolor = kBlueColor;
    shapes[2].bounds = rect2;

    drawShapes (shapes, 3);

    return (0);
} //main</pre>
